<div class="card mt-5">    <div class="card-header">        <div class="d-flex align-items-center pt-2 pb-2">            <div class="d-flex align-items-center">                <?= $title ?>            </div>        </div>    </div>    <div class="card-body">        <form action="<?= base_url('products/edit/' . $product['id']); ?>" method="post" enctype="multipart/form-data">            <label for="name">Name</label>            <input type="text" name="oetProductName" id="oetProductName" class="form-control" value="<?= $product['name'] ?>" />            <br />            <label for="price">Price</label>            <input type="number" name="oetProductPrice" id="oetProductPrice" class="form-control" value="<?= $product['price'] ?>" />            <br />            <label for="description">Description</label>            <textarea name="otaProductDesc" id="otaProductDesc" class="form-control"><?= $product['description'] ?></textarea>            <br />            <label for="category">Category</label>            <select name="ocmProductCategory" id="ocmProductCategory" class="form-control">                <?php                foreach ($categories as $category) {                    echo '<option value="' . $category['id'] . '"' . ($category['id'] == $product['category_id'] ? 'selected="selected"' : "") .'>' . $category['name'] . '</option>';                }                ?>            </select>            <br />            <?php if ($product['image']) { ?>            <img class="pb-2" src="<?= base_url('assets/img/'. $product['image'])?>" width="100" height="100" onerror="imgError(this);">            <?php } ?>            <br />            <input type="file" name="oflProductImage" size="20" />            <br /><br />            <input type="submit" value="Edit" />        </form>    </div></div>