<div class="card mt-5">    <div class="card-header">        <div class="d-flex align-items-center pt-2 pb-2">            <div class="d-flex align-items-center">                <?= $lang['tHeader_title_edit'] ?>            </div>        </div>    </div>    <div class="card-body">        <form action="<?= base_url('products/edit/' . $product['FNPrdId']); ?>" method="post" enctype="multipart/form-data">            <label for="name"><?= $lang['tProduct_code'] ?></label>            <input type="text" name="oetProductCode" id="oetProductCode" class="form-control" value="<?= $product['FTPrdCode'] ?>" disabled/>            <label for="name"><?= $lang['tProduct_name'] ?></label>            <input type="text" name="oetProductName" id="oetProductName" class="form-control" value="<?= $product['FTPrdName'] ?>" />            <br />            <label for="price"><?= $lang['tProduct_price'] ?></label>            <input type="number" name="oetProductPrice" id="oetProductPrice" class="form-control" value="<?= $product['FCPrdPrice'] ?>" />            <br />            <label for="description"><?= $lang['tProduct_description'] ?></label>            <textarea name="otaProductDesc" id="otaProductDesc" class="form-control"><?= $product['FTPrdDescription'] ?></textarea>            <br />            <label for="category"><?= $lang['tProduct_category'] ?></label>            <select name="ocmProductCategory" id="ocmProductCategory" class="form-control">                <?php                foreach ($categories as $category) {                    echo '<option value="' . $category['id'] . '"' . ($category['id'] == $product['FNPrdCatId'] ? 'selected="selected"' : "") .'>' . $category['name'] . '</option>';                }                ?>            </select>            <br />            <?php if ($product['FTPrdImage']) { ?>            <img class="pb-2" src="<?= base_url('application/modules/testproject/assets/img/'. $product['FTPrdImage'])?>" width="100" height="100" onerror="JSCNTPJimgError(this);">            <?php } ?>            <br />            <input type="file" name="oflProductImage" size="20" />            <br /><br />            <input type="submit" value="<?= $lang['tEdit'] ?>" />        </form>    </div></div>